RSpec.describe PharmaciesController, type: :controller do

  # This should return the minimal set of attributes required to create a valid
  # Pharmacy. As you add validations to Pharmacy, be sure to
  # adjust the attributes here as well.
  let(:valid_attributes) {
    {name: "Chemist 22"}
  }

  let(:invalid_attributes) {
    {name: ""}
  }

  # This should return the minimal set of values that should be in the session
  # in order to pass any filters (e.g. authentication) defined in
  # PharmaciesController. Be sure to keep this updated too.
  let(:valid_session) { {} }

  describe "GET #index" do
    it "assigns all pharmacies as @pharmacies" do
      pharmacy = Pharmacy.create! valid_attributes
      get :index, {}, valid_session
      expect(assigns(:pharmacies)).to eq([pharmacy])
    end
  end

  describe "GET #show" do
    it "assigns the requested pharmacy as @pharmacy" do
      pharmacy = Pharmacy.create! valid_attributes
      get :show, {:id => pharmacy.to_param}, valid_session
      expect(assigns(:pharmacy)).to eq(pharmacy)
    end
  end

  describe "GET #new" do
    it "assigns a new pharmacy as @pharmacy" do
      get :new, {}, valid_session
      expect(assigns(:pharmacy)).to be_a_new(Pharmacy)
    end
  end

  describe "GET #edit" do
    it "assigns the requested pharmacy as @pharmacy" do
      pharmacy = Pharmacy.create! valid_attributes
      get :edit, {:id => pharmacy.to_param}, valid_session
      expect(assigns(:pharmacy)).to eq(pharmacy)
    end
  end

  describe "POST #create" do
    context "with valid params" do
      it "creates a new Pharmacy" do
        expect {
          post :create, {:pharmacy => valid_attributes}, valid_session
        }.to change(Pharmacy, :count).by(1)
      end

      it "assigns a newly created pharmacy as @pharmacy" do
        post :create, {:pharmacy => valid_attributes}, valid_session
        expect(assigns(:pharmacy)).to be_a(Pharmacy)
        expect(assigns(:pharmacy)).to be_persisted
      end

      it "redirects to the created pharmacy" do
        post :create, {:pharmacy => valid_attributes}, valid_session
        expect(response).to redirect_to(Pharmacy.last)
      end
    end

    context "with invalid params" do
      it "assigns a newly created but unsaved pharmacy as @pharmacy" do
        post :create, {:pharmacy => invalid_attributes}, valid_session
        expect(assigns(:pharmacy)).to be_a_new(Pharmacy)
      end

      it "re-renders the 'new' template" do
        post :create, {:pharmacy => invalid_attributes}, valid_session
        expect(response).to render_template("new")
      end
    end
  end

  describe "PUT #update" do
    context "with valid params" do
      let(:new_attributes) {
        {name: "Chemist 23"}
      }

      it "updates the requested pharmacy" do
        pharmacy = Pharmacy.create! valid_attributes
        put :update, {:id => pharmacy.to_param, :pharmacy => new_attributes}, valid_session
        pharmacy.reload
        expect(pharmacy.name).to eq "Chemist 23"
      end

      it "assigns the requested pharmacy as @pharmacy" do
        pharmacy = Pharmacy.create! valid_attributes
        put :update, {:id => pharmacy.to_param, :pharmacy => valid_attributes}, valid_session
        expect(assigns(:pharmacy)).to eq(pharmacy)
