      it "redirects to the pharmacy" do
        pharmacy = Pharmacy.create! valid_attributes
        put :update, {:id => pharmacy.to_param, :pharmacy => valid_attributes}, valid_session
        expect(response).to redirect_to(pharmacy)
      end
    end

    context "with invalid params" do
      it "assigns the pharmacy as @pharmacy" do
        pharmacy = Pharmacy.create! valid_attributes
        put :update, {:id => pharmacy.to_param, :pharmacy => invalid_attributes}, valid_session
        expect(assigns(:pharmacy)).to eq(pharmacy)
      end

      it "re-renders the 'edit' template" do
        pharmacy = Pharmacy.create! valid_attributes
        put :update, {:id => pharmacy.to_param, :pharmacy => invalid_attributes}, valid_session
        expect(response).to render_template("edit")
      end
    end
  end

  describe "DELETE #destroy" do
    it "destroys the requested pharmacy" do
      pharmacy = Pharmacy.create! valid_attributes
      expect {
        delete :destroy, {:id => pharmacy.to_param}, valid_session
